    MoviesJsonParser parser = new MoviesJsonParser();

    @Test
    public void testShouldParseAllItems() throws Exception {

        ArrayList<Movie> result = parser.Parse(testInput);

        Assert.assertEquals(20, result.size());
    }

    @Test
    public void testShouldParseMovieTitle() throws Exception {

        ArrayList<Movie> result = parser.Parse(testInput);

        Assert.assertEquals("Ant-Man", result.get(0).getTitle());
    }

    @Test
    public void testShouldParseMovieReleaseDate() throws Exception {

        ArrayList<Movie> result = parser.Parse(testInput);

        SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");

        java.util.Date expected = format.parse("2015-07-01");

        Assert.assertEquals(expected, result.get(1).getReleaseDate());
    }

    @Test
    public void testShouldParsePosterUrl() throws Exception {

        ArrayList<Movie> result = parser.Parse(testInput);

        Assert.assertEquals("/5JU9ytZJyR3zmClGmVm9q4Geqbd.jpg", result.get(1).getPosterUrl());
    }

    @Test
    public void testShouldParseVoteAvg() throws Exception {

        ArrayList<Movie> result = parser.Parse(testInput);

        Assert.assertEquals(6.9f, result.get(2).getVoteAvg());
    }

